'use strict';

var asciidoctor = require('asciidoctor.js')();

var _require = require('../'),
    State = _require.State,
    Deserializer = _require.Deserializer;

var html = require('../html');

/**
 * Render Asciidoc to HTML.
 * @param  {String} content
 * @return {String} html
 */
function asciidocToHTML(content) {
    return asciidoctor.convert(content, {
        'attributes': 'showtitle'
    });
}

/**
 * Deserialize an Asciidoc string
 * @type {Deserializer}
 */
var deserialize = Deserializer().then(function (state) {
    var htmlContent = asciidocToHTML(state.text);
    var htmlState = State.create(html);

    var nodes = htmlState.deserialize(htmlContent);

    return state.push(nodes).skip(state.text.length);
});

module.exports = deserialize;